var fs = require('fs');

module.exports.createLayoutAndIndexFiles = function(path){
	var layout = fs.readFileSync(__dirname + "/jadelayout.txt");
	fs.writeFileSync(path + "/views/layout.jade",layout);

	var index = fs.readFileSync(__dirname + "/jadeindex.txt");
	fs.writeFileSync(path + "/views/index.jade",index);
}
