var fs = require('fs');

module.exports.createLayoutAndIndexFiles = function(path){
	var layout = fs.readFileSync(__dirname + "/jshtmllayout.txt");
	fs.writeFileSync(path + "/views/layout.jshtml",layout);

	var index = fs.readFileSync(__dirname + "/jshtmlindex.txt");
	fs.writeFileSync(path + "/views/index.jshtml",index);
}
